import React, { useState,  useEffect } from 'react';

// Рендер карточек
function RenderCard() {

  // Запрос
  const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
  
    useEffect(() => {
      fetch("http://127.0.0.1:5000/?page=1")
        .then(res => res.json())
        .then(
          (result) => {
            setIsLoaded(true);
            setItems(result);
          },
          (error) => {
            setIsLoaded(true);
            setError(error);
          }
        )
  }, [])


  return (
    <div className="card-block" onClick={''}>
      <div className="avatar">
        <img className="avatar-img" src="" alt="avatar"></img>
      </div>
      <p className="first-name">{items.map(item => item.first_name)}</p>
      <p className="last-name">{items.map(item => item.last_name)}</p>
    </div>
  );
}


// Рендер главной страницы
function HomePage() {
  return (
    <div>
      <div className="header">
        <h1>Карточки</h1>
      </div>
  
      <div className="container">
        <div className="previous-page">
          <button className="btn-previous-page" onClick={''}>&#129044;</button>
        </div>
  
        <div className="cards">
          <RenderCard />
          {/* <RenderCard />
          <RenderCard />
          <RenderCard />
          <br/>
          <RenderCard />
          <RenderCard />
          <RenderCard />
          <RenderCard />
          <br/>
          <RenderCard />
          <RenderCard />
          <RenderCard />
          <RenderCard /> */}
        </div>
  
        <div className="next-page">
          <button className="btn-next-page" onClick={''}>&#129046;</button>
        </div>
      </div>    
    </div>
  );
}
  
export default HomePage;
