            //                 <p className="last-name-cp">{items.map(item => item.last_name)}</p>
            //             </div>
            //         </div>

            //         <div className="block-body-cp">
            //             <div className="body-left-cp">
            //                 <p>Email:</p>
            //                 <p>Телефон:</p>
            //                 <p>Пол:</p>
            //                 <p>Язык:</p>
            //                 <p>Работа:</p>
            //                 <p>Университет:</p>
            //             </div>
            //             <div className="body-right-cp">
            //                 <p className="email-cp">{items.map(item => item.email)}</p>
            //                 <p className="phone-cp">{items.map(item => item.phone)}</p>
            //                 <p className="gender-cp">{items.map(item => item.gender)}</p>
            //                 <p className="lenguage-cp">{items.map(item => item.language)}</p>
            //                 <p className="job-cp">{items.map(item => item.job)}</p>
            //                 <p className="university-cp">{items.map(item => item.university)}</p>
            //             </div>
            //         </div>
            //     </div>
            // </div>
            console.log(items.map(item => item.first_name))
        );
    }
}

export default RenderCardPage;
